def arc(r,c,c2, x, y, d=True, ry=False,t=10):
 for i in range(x,x+r+1)if d else range(x,x-r-1,-1):
  h=int(sqrt(r**2-(i-x)**2))
  if ry:
   for j in range(y-h,y+h+1):
    color=c if(i+j)//t%2==0 else c2
    fr(i,j,1,1,color)
  else:fr(i,y-h,1,2*h,c)
